package completejava;

public class RectangularStarPattern {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		int i,j;
		
		for(i=0;i<4;i++)
		{
			for(j=0;j<3;j++)
			{
				System.out.print("*");
			}
			System.out.print("\n");
		}
		
	}

}
